import { Component } from 'react';
import { musicalInstruments } from '../data/musicalInstruments';
import WithRouter, { IWithRouterProps } from './WithRouter';
import Form from 'react-bootstrap/Form'
import { Button } from 'react-bootstrap';
import {IMusicalInstruments} from '../interfaces/IMusicalInstrument';


interface IEditProps {
  music:IMusicalInstruments;
}

const EditWithRouter = WithRouter(class Edit extends Component<IWithRouterProps/* & IEditProps*/, any> {
  id = parseInt(this.props.params.id ? this.props.params.id : "0");

  state = {
    musicalInstrument: { id: 0, instrument: "", description: "", price: 0 }
  }

  async componentDidMount() {
    const id = parseInt(this.props.params.id ? this.props.params.id : "0");
    const response = await fetch(`http://localhost:8080/music/${id}`);
    const data = await response.json();
    this.setState({ musicalInstrument: data })
  }
  async writeToMusInstFile() {
    // POST request using fetch with async/await
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ instrument: this.state.instrument, description: this.state.description, price: this.state.price})
        };
        const response = await fetch(`http://localhost:8080/music/${this.id}`, requestOptions);
        const data = await response.json();
        this.setState({ postId: data.id });
    }


  render() {
    const handleChange = (changedValue: string, field: string) => {
      console.log(changedValue);
      switch (field) {
        case "instrument":
          this.setState({ instrument: changedValue });
          break;
        case "description":
          this.setState({ description: changedValue });
          break;
        case "price":
          this.setState({ price: changedValue });
          break;
      }
    };

    return (
      <div>
        <Form onSubmit={this.writeToMusInstFile}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Control type="text" value={this.state.instrument} onChange={(e) => handleChange(e.target.value, "instrument")} />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Control type="text" value={this.state.description} onChange={(e) => handleChange(e.target.value, "description")} />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Control type="number" value={this.state.price} onChange={(e) => handleChange(e.target.value, 'price')} />
          </Form.Group>
          <Button variant="primary" type="submit">Submit</Button>
        </Form>
      </div>
    );
  }
})
export default EditWithRouter;
