import Edit from './components/Edit';
import About from './components/About';
import { Routes, Route, Link } from "react-router-dom";

const routes = [{
  route: 'homePage',
  Component: HomePage,
  name: 'Home',
}, {
  route: 'view',
  Component: View,
  name: 'View',
}, {
  route: 'edit',
