import React from 'react';
import { musicalInstruments } from '../data/musicalInstruments';
import WithRouter, { IWithRouterProps } from './WithRouter'
const ViewWithRouter = WithRouter(class View extends React.Component<IWithRouterProps, any> {
  state = {
    musicalInstrument: null
  }

  async componentDidMount() {
    const id = parseInt(this.props.params.id ? this.props.params.id : "0");
    const response = await fetch(`http://localhost:8080/music/${id}`);
    const data = await response.json();
    this.setState({ musicalInstrument: data })
  }
  render() {
    const {musicalInstrument} = this.state;
    return <div>
      <h1>{musicalInstrument ? musicalInstrument.instrument : ""}</h1>
      <p>{musicalInstrument.description}</p><br />
      <p>price: {musicalInstrument.price}</p>
    </div>
  }
})
export default ViewWithRouter;
