import React, { Component } from 'react';
import { musicalInstruments } from '../data/musicalInstruments';

interface ViewProps {
  id: number;
}
export default class Edit extends Component<{}, any> {
 
  constructor(props: any) {
    super(props)
    this.state = { instrument: "violin", description: "bla bla", price: 10 };
  }

  render() {
    // ðŸ‘‡ï¸ called every time input's value changes
    // let instrument: string = "violin";
    const handleChange = (changedValue: string, field: string) => {
      console.log(changedValue);
      switch (field) {
        case "instrument":
          this.setState({ instrument: changedValue });
          break;
        case "description":
          this.setState({ description: changedValue });
          break;
        case "price":
          this.setState({ price: changedValue });
          break;
      }
    };

    const writeToMusInstFile = () => {
      musicalInstruments[0].instrumunt = this.state.instrument;
      musicalInstruments[0].description = this.state.description;
      musicalInstruments[0].price = this.state.price;
    }

    return (
      <div>
        <form onSubmit={writeToMusInstFile}>
          <input type="text" value={this.state.instrument} onChange={(e) => handleChange(e.target.value, "instrument")} />
          <input type="text" value={this.state.description} onChange={(e) => handleChange(e.target.value, "description")} />
          <input type="number" value={this.state.price} onChange={(e) => handleChange(e.target.value, 'price')} />
          <input type="submit" />
        </form>
      </div>
    );

    // return (
    //   <div>
    //     <form action="" method="post">
    //       <input type="text" placeholder='instrument'/>
    //       <input type="text" placeholder='descrition' />
    //       <input type="number" placeholder='price'/>
    //     </form>
    //   </div>
    // );
  }
}
