import React, { Component } from 'react';
import { musicalInstruments } from '../data/musicalInstruments';
import View from './View';
import Edit from './Edit';
import { Link, Outlet } from 'react-router-dom';
export default class HomePage extends Component<{}, any> {

  constructor(props: any) {
    super(props)
    this.state = { filteredList: [], selectedIM: "" };
  }
  // changeState = (id: number) => {
  //   console.log("in on click" + id);
  //   this.setState({id});
  // };



  render() {
    const search = () => {
      musicalInstruments.filter(im=> im.instrument == this.state.selectedIM).map(filteredMI => (
        <li>
          {filteredMI.instrument}
        </li>
      ))
    }

    return (
      <div>
        <h2>MUSICALL</h2>
        <h3> There are {musicalInstruments.length} musical instruments in store  </h3>
        {/*<View id={this.state.id} />*/}
         {/* <Edit id={this.state.id} />  */}
        <form onSubmit={search}>
          <input type="text" name="search" placeholder='enter nusical instrument to search'/>
          <input type="submit" />
         </form>
        <ul>
          {musicalInstruments.map((innerItem, i) => <li> <Link to={"/view/" + ( i )}> {innerItem.instrument}</Link> </li>)}
          {/* <button onClick={() => this.changeState(i)}>edit</button> */}
          {/* <button> <Link to={"/product" + ( i )}>Enter </Link> </button> */}
          
        </ul>
        <ul className="products">
            { musicalInstruments.map((innerItem, i) => <li key={innerItem}> 
            <Link to={"/view/" + (i )}>{i + 1} </Link></li>) }
        </ul>
        <Outlet />

      </div>
    );
  }
}
